------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
	If NOT exists (select * from TABLETYPE where TABLETYPE = -514)
        	BEGIN
         	 PRINT '**** RFC42101 Adding data TABLETYPE.TABLETYPE = -514'
		 INSERT INTO TABLETYPE (TABLETYPE, TABLENAME, MODIFIABLE, ACTIVITYFLAG, DATABASETABLE)
		 VALUES (-514, N'Schema Mapping', 0, 0, N'TABLECODES')
        	 PRINT '**** RFC42101 Data successfully added to TABLETYPE table.'
		 PRINT ''
         	END
    	ELSE
         	PRINT '**** RFC42101 TABLETYPE.TABLETYPE = -514 already exists'
         	PRINT ''
    	go
